	(15, 'Registration mail', 'Registration mail', 'register', '0', '<table style="border: 1px solid #ccc; background: #fff; width: 600px;" cellpadding="0" cellspacing="0">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border="0" cellpadding="0" cellspacing="0">\r\n<tbody>\r\n<tr>\r\n<td style="width: 20px;"></td>\r\n<td>\r\n<table style="width: 100%;">\r\n<tbody>\r\n<tr>\r\n<td width="400px"><a href="http://redcomponent.com" target="_blank"> <img src="http://redcomponent.com/images/redcomponent_logo.png" style="width: 340px; height: auto; padding-top: 20px;" /> </a></td>\r\n<td style="font-size: 12px; color: #878787; float: right;"></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n<td style="width: 20px;"></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table style="width: 100%;" cellpadding="0" cellspacing="0">\r\n<tbody>\r\n<tr>\r\n<td style="height: 20px; background-color: #444544;"></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border="0" cellpadding="0" cellspacing="0">\r\n<tbody>\r\n<tr>\r\n<td colspan="3" style="height: 20px;"></td>\r\n</tr>\r\n<tr>\r\n<td style="width: 20px;"></td>\r\n<td>\r\n<p style="font-size: 24px; font-family: verdana; color: #616161; text-align: justify;">Thank you for your registration!</p>\r\n<p style="font-size: 14px; font-family: verdana; color: #616161; text-align: justify;">You are now a registered user at redCOMPONENT.</p>\r\n<p style="font-size: 14px; font-family: verdana; color: #616161; text-align: justify; line-height: 20px;">To go to your account where you can make all necessary adjustments, such as change your address book, view old orders, download your purchased products, or sign up for our newsletter please click {account_link}.</p>\r\n<p style="font-size: 14px; font-family: verdana; color: #616161; text-align: justify;">�</p>\r\n<p style="font-size: 14px; font-family: verdana; color: #616161; text-align: justify;"><strong>User information:</strong></p>\r\n<p style="font-size: 14px; font-family: verdana; color: #616161; text-align: j